
//listing of all techniques and passes with embedded asm listings 

technique PostProcess
{
    pass p0
    {
        //No embedded vertex shader
        pixelshader = 
            asm {
            //
            // Generated by Microsoft (R) HLSL Shader Compiler 10.1
            //
            // Parameters:
            //
            //   float Luminance;
            //
            //
            // Registers:
            //
            //   Name         Reg   Size
            //   ------------ ----- ----
            //   Luminance    c0       1
            //
            
                preshader
                add r0.x, c0.x, (0.00100000005)
                rcp c0.x, r0.x
            
            // approximately 2 instructions used
            //
            // Generated by Microsoft (R) HLSL Shader Compiler 10.1
            //
            // Parameters:
            //
            //   sampler2D g_samSrcColor;
            //
            //
            // Registers:
            //
            //   Name          Reg   Size
            //   ------------- ----- ----
            //   g_samSrcColor s0       1
            //
            
                ps_2_0
                def c1, 0.180000007, 1.5625, 1, 0
                dcl t0.xy
                dcl_2d s0
                texld r0, t0, s0
                mul r0, r0, c1.x
                mul r0, r0, c0.x
                mad r1, r0, c1.y, c1.z
                mul r2, r0, r1
                mad r0, r0, r1, c1.z
                rcp r1.x, r0.x
                rcp r1.y, r0.y
                rcp r1.z, r0.z
                rcp r1.w, r0.w
                mul r0, r1, r2
                mov oC0, r0
            
            // approximately 12 instruction slots used (1 texture, 11 arithmetic)
            };
    }
}

