
//listing of all techniques and passes with embedded asm listings 

technique PostProcess
{
    pass p0
    {
        //No embedded vertex shader
        pixelshader = 
            asm {
            //
            // Generated by Microsoft (R) HLSL Shader Compiler 10.1
            //
            // Parameters:
            //
            //   float4 FocalPlane;
            //   sampler2D g_samSceneColor;
            //   sampler2D g_samSrcColor;
            //   sampler2D g_samSrcPosition;
            //
            //
            // Registers:
            //
            //   Name             Reg   Size
            //   ---------------- ----- ----
            //   FocalPlane       c0       1
            //   g_samSrcColor    s0       1
            //   g_samSrcPosition s1       1
            //   g_samSceneColor  s2       1
            //
            
                ps_2_0
                def c1, 1, 0, 0, 0
                dcl t0.xy
                dcl t1.xy
                dcl_2d s0
                dcl_2d s1
                dcl_2d s2
                texld r0, t0, s1
                texld r1, t1, s2
                texld r2, t0, s0
                dp4 r1.w, r0, c0
                abs_sat r1.w, r1.w
                lrp r0.xyz, r1.w, r2, r1
                mov r0.w, c1.x
                mov oC0, r0
            
            // approximately 8 instruction slots used (3 texture, 5 arithmetic)
            };
    }
}

